import java.util.*;public class Main{    public static void main(String args[]){        Scanner sc= new Scanner(System.in);        int n=sc.nextInt();        int k=sc.nextInt();                    int arr[]=new int[n];            int v,count=0;            for(int i=0;i<n;i++){                arr[i]=sc.nextInt();            }                v=arr[k-1];            for(int i=0;i<n;i++){                if(arr[i] >= v && arr[i] >0 ){                    count++;                }            }                    System.out.println(count);            }}