import java.util.*;public class main{    public static void main(String args[]){        Scanner sc = new Scanner(System.in);        int t=sc.nextInt();        while(t-- >0){            int sum=0;            int n=sc.nextInt();            String s =sc.next();            for(int i=0;i<n;i++){                boolean k=true;                for(int j=0;j<i;j++){                    if(s.charAt(i)==s.charAt(j)){                        k=false;                                            }                }                if(k){                    sum+=2;                }else{                    sum+=1;                }            }            System.out.println(sum);        }    }}