import java.util.*;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int a[][]=new int[5][5];        for(int i=0;i<5;i++){            for(int j=0;j<5;j++){                a[i][j]=sc.nextInt();            }        }         int count=0;        for(int i=0;i<5;i++){            for(int j=0;j<5;j++){                if(a[i][j]==1){                    int v=Math.abs(i-2);                    int l=Math.abs(j-2);                    count=v+l;                    break;                }            }        }         System.out.println(count);    }}